$(document).ready(function(){$(".more-categories").click(function(){$(".hidden-categories").slideDown(1e3),$(this).slideUp(500).removeClass("button")});var e=$(".current-lang img").attr("alt");$.ajax({url:"/site/ajax-search?lang="+e,type:"get",success:function(e){$("#searchInput").autoComplete({minChars:2,source:function(t,n){t=t.toLowerCase();var a=JSON.parse(e),r=[];for(i=0;i<a.length;i++)~a[i].toLowerCase().indexOf(t)&&r.push(a[i]);n(r)},onSelect:function(){$("#searchForm").submit()}})}}),$("#nivoSlider").nivoSlider({effect:"random",slices:15,boxCols:12,boxRows:6,animSpeed:1e3,pauseTime:6e3,directionNav:!0,controlNav:!0,pauseOnHover:!0,prevText:'<i class="icon-angle-left"></i>',nextText:'<i class="icon-angle-right"></i>',afterLoad:function(){$("#slider").find(".nivo-caption").addClass("slider-caption-bg")}})});
